import React, { Component } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import { red } from "@material-ui/core/colors";
import Button from "@material-ui/core/Button";
import SmartphoneIcon from "@material-ui/icons/Smartphone";
import { Link } from 'react-router-dom';
//import productStore from 'store/modules/productStore';
import { connect } from 'react-redux';
import { changeProductList, changeCurrentProduct, changeCategory } from 'store/modules/productStore';
import ProductList_Category from 'components/redux/ProductList_Category';

//import { handleClick } from 'container/ProductListContainer';
//front-end\src\container\ProductListContainer.js
const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 0,
    paddingTop: "56.25%", // 16:9
  },
  expand: {
    transform: "rotate(0deg)",
    marginLeft: "auto",
    transition: theme.transitions.create("transform", {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: "rotate(180deg)",
  },
  avatar: {
    backgroundColor: red[500],
  },
  margin: {
    marginLeft: theme.spacing(2),
  },
}));

const ProductCard = (props) => {
  const classes = useStyles();
  const bull = <span className={classes.bullet}>•</span>;
  const { id, avatarSrc, title, subtitle, description, imgSrc, price } = props;
  const onClickHandler = () => {

    const { changeCategory } = this.props;
    changeCategory("test");
    //제대로 넘어오는거니까 이 정보를 넘기면된다. 
    //Store에 저장하는 
    console.log("storeTest" + changeCurrentProduct);
    console.log("storeTest" + changeProductList);

  }
  return (
    <Card className={classes.root}>
      <CardHeader
        avatar={<Avatar src={avatarSrc} />}
        action={
          <IconButton aria-label="settings">
            <SmartphoneIcon />
          </IconButton>
        }
        title={title}
        subheader={subtitle}
      />
      <CardMedia style={{ height: "150px" }} image={imgSrc} />
      <CardContent>
        <Typography variant="body2" component="p">
          {description}
        </Typography>
      </CardContent>
      <CardActions>
        <Link to="/detail">
          <Button
            variant="contained"
            size="medium"
            color="primary"
            className={classes.margin}
            onClick={onClickHandler}
          // onClick={changeCategory("test")}

          >
            Detail
          </Button>
        </Link>
        <Link to="www.naver.com">
          <Button
            variant="contained"
            size="medium"
            color="primary"
            className={classes.margin}
          >
            BUY NOW
        </Button>
        </Link>
      </CardActions>
    </Card>
  );
};

const mapStateToProps = ({ productStore }) => ({  //2
  category: productStore.category,
  list: productStore.productList,
  CurrentProduct: productStore.CurrentProduct,
});

const mapDispatchToProps = dispatch => {
  return {
    changeCurrentProduct: CurrentProduct => dispatch(changeCurrentProduct(CurrentProduct)),
  }
};

export default connect( // 스토어와 연결
  mapStateToProps,
  mapDispatchToProps,
)(ProductCard);
