import React, { useState, useEffect, useSelector } from 'react';
import Posts from './Posts';

import PaginationPage from './PaginationPage';
import axios from 'axios';
import { connect } from 'react-redux';
import productStore from 'store/modules/productStore';
import { changeProductList, changeCurrentProduct } from 'store/modules/productStore';
import ApiService from "ApiService";
import SearchBar from 'components/Search/SearchBar';
const PaginationProductList = (props) => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage] = useState(12);

  const fetchPosts = () => {
    setPosts(props.list);

  }

  useEffect(() => {
    fetchPosts();

    console.log("props.list", props.list);
    //변화를 감지하거나, 컴포넌트를 부를때 마다 실행 즉 화면이 바뀌면 한번은 실행하게된다. 
  }, [props.list]);



  // Get current posts
  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const test = posts.filter(posts => posts.name.indexOf(props.searchKeyword) >= 0);
  //일단 서치바로 한번 거른다음에 페이지네이션 시킨다.!!!!!!
  const qwer = posts.filter(posts => posts.price.slice(posts.price.indexOf(",", 1, "")));
  console.log("qwer", qwer);
  posts.map((post => {
    post.price.indexOf(",", 1, "");
    //post.price.indexOf(",", 1, "");
    console.log("asdf", post.price.replace(asdf));
  }));

  const currentPosts = test.slice(indexOfFirstPost, indexOfLastPost);
  //const currentPosts = posts.slice(indexOfFirstPost, indexOfLastPost);

  // Change page
  const paginate = pageNumber => setCurrentPage(pageNumber);

  return (
    <div className='container mt-5'>
      <h1 className='text-primary mb-3'>Hello, World!</h1>
      <SearchBar />
      <Posts posts={currentPosts} />
      <PaginationPage
        postsPerPage={postsPerPage}
        totalPosts={posts.length}
        paginate={paginate}
      />
    </div>
  );
};

const mapStateToProps = ({ productStore }) => ({  //2
  category: productStore.category,
  list: productStore.productList,
  CurrentProduct: productStore.CurrentProduct,
  searchKeyword: productStore.searchKeyword,
});
//import store name이 맞는거다

//props로 넣어줄 액션 생성함수
const mapDispatchToProps = dispatch => ({
  changeProductList: list => dispatch(changeProductList(list)),
  changeCurrentProduct: CurrentProduct => dispatch(changeCurrentProduct(CurrentProduct)),
});

export default connect( // 스토어와 연결
  mapStateToProps,
  mapDispatchToProps,
)(PaginationProductList);
