    return (
      <Grid item xs={12} sm={4}>
        <ProductCard {...object} />
      </Grid>
    );
  };

  return (
    <Grid container spacing={4}>
      {productList.map((object) => getProductList(object))}
    </Grid>
  );
};
*/
export default Content;
