import React from 'react';
import styled from "styled-components";
import categories from './categories';
import Test from './test';
import CustomTabs from 'components/CustomTabs/CustomTabs';
//카테고리 리스트를 받아와서 출력한다
//onclick된거로 값이 바뀐다.
//productList가 onclick된 카테고리로 바뀌려면?
//카테고리 값마다 불러오는 productList가 달라야 한다?
//그렇다면 productList_Category에 각 값마다 달라져야하는 데이터를 json형식으로 저장해두고
//카테고리가 변화하면 안에 list도 그 변화를 감지하고 바뀌어야 한다.
//와우
import styles from "assets/jss/material-kit-react/views/componentsSections/tabsStyle.js";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles(styles);


//
const CategoriesBlock = styled.div`
  display: flex;
  padding: 1rem;
  width: 768px;
  margin: 0 auto;
  @media screen and (max-width: 768px) {
    width: 100%;
    overflow-x: auto;
  }
`;
const CategoryNavbar = styled.div`
  font-size: 1.125rem;
  cursor: pointer;
  white-space: pre;
  text-decoration: none;
  color: inherit;
  padding-bottom: 0.25rem;
  &:hover {
    color: #495057;
  }
  &.active {
    font-weight: 600;
    border-bottom: 2px solid #22b8cf;
    color: #22b8cf;
    &:hover {
      color: #3bc9db;
    }
  }
  & + & {
    margin-left: 1rem;
  }
`;


const CategoryItem = ({ category, active, onClick }) => {
    return (
        <>
            <CategoryNavbar
                key={category.name}
                activeClassName="active"
                onClick={onClick}
                active={category.name === "all" ? "/" : `/${category.name}`}
            >
                {category}
            </CategoryNavbar>
        </>
    );
};
//plainTabs tabs ={[ 내용 ]}
const Category = ({ selected, onSelect }) => {
    const classes = useStyles();
    return (
        <>
            <CategoriesBlock>
                {categories.map(category => (
                    <CategoryItem
                        category={category}
                        active={selected === category}
                        onClick={() => onSelect(category)}
                    />
                ))}
            </CategoriesBlock>
            <Test
                headerColor="primary"
                tabs={[
                    {
                        tabName: "Profile",

                        tabContent: (
                            <p className={classes.textCenter}>
                                I think that’s a responsibility that I have, to push
                                possibilities, to show people, this is the level that
                                things could be at. So when you get something that has
                                the name Kanye West on it, it’s supposed to be pushing
                                the furthest possibilities. I will be the leader of a
                                company that ends up being worth billions of dollars,
                                because I got the answers. I understand culture. I am
                                the nucleus.
                            </p>
                        )
                    },
                    {
                        tabName: "Messages",

                        tabContent: (
                            <p className={classes.textCenter}>
                                I think that’s a responsibility that I have, to push
                                possibilities, to show people, this is the level that
                                things could be at. I will be the leader of a company
                                that ends up being worth billions of dollars, because I
                                got the answers. I understand culture. I am the nucleus.
                                I think that’s a responsibility that I have, to push
                                possibilities, to show people, this is the level that
                                things could be at.
                            </p>
                        )
                    },
                    {
                        tabName: "Settings",

                        tabContent: (
                            <p className={classes.textCenter}>
                                think that’s a responsibility that I have, to push
                                possibilities, to show people, this is the level that
                                things could be at. So when you get something that has
                                the name Kanye West on it, it’s supposed to be pushing
                                the furthest possibilities. I will be the leader of a
                                company that ends up being worth billions of dollars,
                                because I got the answers. I understand culture. I am
                                the nucleus.
                            </p>
                        )
                    }
                ]}
            />
        </>
    )
}

export default Category;
