import React from 'react';
import { Radar } from 'react-chartjs-2';
import clsx from 'clsx';
import PropTypes from 'prop-types';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import {
    Card,
    CardHeader,
    CardContent,
    IconButton,
    Divider,
    Typography
} from '@material-ui/core';
import RefreshIcon from '@material-ui/icons/Refresh';
import { Grid } from "@material-ui/core";
import { connect } from 'react-redux';
import productStore from 'store/modules/productStore';
import { changeCurrentKeyword, changeCurrentProduct, changeReview } from 'store/modules/productStore';
import testData from './data';
const useStyles = makeStyles(theme => ({
    root: {
        height: '100%'
    },
    chartContainer: {
        position: 'relative',
        height: '300px'
    },
    stats: {
        marginTop: theme.spacing(2),
        display: 'flex',
        justifyContent: 'center'
    },
    device: {
        textAlign: 'center',
        padding: theme.spacing(1)
    },
    deviceIcon: {
        color: theme.palette.icon
    }
}));

const RadarChart = props => {
    const { className, ...rest } = props;
    const classes = useStyles();
    const theme = useTheme();


    //console.log("labeldata    -> " + props.graphData.labels);
    // console.log("graphData    -> " + JSON.stringify(props.graphData));
    // console.log("currentProduct    -> " + props.CurrentProduct);

    let titleString = props.CurrentProduct + "'s Keyword";

    const data = {
        datasets: [
            {
                data: props.reviewNumber, // store에서 가져온 값 이걸 바꾸면 차트도 바뀌어야 됨 !!!!!!!!!!!!!!
                backgroundColor: ['#e53935', '#8e24aa', '#9fa8da', '#b2dfdb', '#80cbc4', '#e6ee9c', '#ffcc80', '#fff59d', '#90a4ae'],
                borderWidth: 8,
                borderColor: theme.palette.white,
                hoverBorderColor: theme.palette.white
            }
        ],
        labels: props.reviewLabels // 보여주는 라벨들
    };

    const options = {
        legend: {
            display: true
        },
        responsive: true,
        maintainAspectRatio: false,
        animation: false,
        cutoutPercentage: 80,
        layout: { padding: 0 },

        tooltips: {
            enabled: true,
            mode: 'index',
            intersect: false,
            borderWidth: 1,
            borderColor: theme.palette.divider,
            backgroundColor: theme.palette.white,
            titleFontColor: theme.palette.text.primary,
            bodyFontColor: theme.palette.text.secondary,
            footerFontColor: theme.palette.text.secondary
        },

    };

    return (
        <Card
            {...rest}
            className={clsx(classes.root, className)}
        >
            <CardHeader
                action={
                    <IconButton size="small">
                        <RefreshIcon />
                    </IconButton>
                }
                title={titleString}
            />
            <Divider />
            <CardContent>
                <div className={classes.chartContainer}>
                    <h2>Radar Example</h2>
                    <Radar data={data} />
                </div>
            </CardContent>
        </Card >
    );
};

RadarChart.propTypes = {
    className: PropTypes.string
};
const mapStateToProps = ({ productStore }) => ({  //2
    CurrentProduct: productStore.CurrentProduct,
    CurrentKeyword: productStore.CurrentKeyword,
    reviewNumber: productStore.reviewNumber,
    reviewLabels: productStore.reviewLabels,
    ReviewData: productStore.ReviewData,
});

const mapDispatchToProps = dispatch => {
    return {
        changeCurrentProduct: CurrentProduct => dispatch(changeCurrentProduct(CurrentProduct)),
        changeCurrentKeyword: CurrentKeyword => dispatch(changeCurrentKeyword(CurrentKeyword)),
        changeReview: ReviewData => dispatch(changeReview(ReviewData)),
    }
};

export default connect( // 스토어와 연결
    mapStateToProps,
    mapDispatchToProps,
)(RadarChart);
