import React, { useState, useEffect } from 'react';
import Posts from './Posts';

import PaginationPage from './PaginationPage';
import axios from 'axios';
import { connect } from 'react-redux';
import productStore from 'store/modules/productStore';
import { changeProductList, changeCurrentProduct } from 'store/modules/productStore';
import ApiService from "ApiService";
import { useSelector, useActions } from 'react-redux';
const PaginationProductList = (props) => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage] = useState(12);
  const { list } = useSelector(state => state.productList, []);

  useEffect(() => {
    const fetchPosts = async () => {
      setLoading(true);
      const res = await axios.get('http://localhost:8080/productlist');
      // 이게 store랑 연결이 되어야 하는데 안되어있음 그래서 카테고리 바꾸면 api부를때 변화가 눈에 안보임

      //const res = props.list;
      //console.log("productListasdf", res.data);
      setPosts(res.data);
      // console.log("posts", posts);
      console.log("res", res.data);
      console.log("list", list);
      //  console.log("props.list", props.list);

      setLoading(false);
    };

    fetchPosts();
  }, []);


  // Get current posts
  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const currentPosts = posts.slice(indexOfFirstPost, indexOfLastPost);

  // Change page
  const paginate = pageNumber => setCurrentPage(pageNumber);

  return (
    <div className='container mt-5'>
      <h1 className='text-primary mb-3'>My Blog</h1>
      <Posts posts={currentPosts} loading={loading} />
      <PaginationPage
        postsPerPage={postsPerPage}
        totalPosts={posts.length}
        paginate={paginate}
      />
    </div>
  );
};

const mapStateToProps = ({ productStore }) => ({  //2
  category: productStore.category,
  list: productStore.productList,
  CurrentProduct: productStore.CurrentProduct,
});
//import store name이 맞는거다

//props로 넣어줄 액션 생성함수
const mapDispatchToProps = dispatch => ({
  changeProductList: list => dispatch(changeProductList(list)),
  changeCurrentProduct: CurrentProduct => dispatch(changeCurrentProduct(CurrentProduct)),
});
export default connect( // 스토어와 연결
  mapStateToProps,
  mapDispatchToProps,
)(PaginationProductList);
