// 초기 상태 정의

const initialState = {
    category: "all",
    productList: [
        {
            id: "id",
            title: "title",
            subtitle: "subtitle"
        },
        {
            id: "id",
            title: "title",
            subtitle: "subtitle"
        },
        {
            id: "id",
            title: "title",
            subtitle: "subtitle"
        },
    ],

};



// 리듀서 작성
//상태변화를 일으키는 함수이다.
//리듀서는 파라미터를 두개 받는다
//첫번째는 현재상태, 두번째는 액션 객체이다.

export default function ProductList(state = initialState, action) {
    return state;
}
